    const getPokemon = async (id) => {
    const url = `https://pokeapi.co/api/v2/pokemon/${id}`
    const res = await fetch(url)
    const data = await res.json()
    
    setPokemonData(data)
   
  }
   
      React.useEffect(() => {
        getPokemon(pokemonId)  
    }, [])  
    

  

console.log(pokemonData)

